export const getTranslator = (lang: string): Translator => {
    const langKey = lang.split('-')[0];
    const dict = translations[langKey] || translations.vi;

    return (key: string, params?: Record<string, string>): string => {
        let translation = dict[key] || key;
        if (params) {
            Object.keys(params).forEach(pKey => {
                const regex = new RegExp(`\\{${pKey}\\}`, 'g');
                translation = translation.replace(regex, params[pKey]);
            });
        }
        return translation;
    };
};